CREATE TABLE items(
    item_id        INT             AUTO_INCREMENT,
    user_id        INT             NOT NULL,
    category_id    INT             NOT NULL,
    item_name      VARCHAR(100)    NOT NULL,
    item_class     VARCHAR(15)     NOT NULL,
    item_size      VARCHAR(2)      NOT NULL,
    item_money     INT             NOT NULL,
    item_feature   VARCHAR(255)    NOT NULL,
    item_image     MEDIUMBLOB      NOT NULL,
    item_fitting   MEDIUMBLOB      NOT NULL,
    item_time      TIMESTAMP            NOT NULL,
    PRIMARY KEY (item_id),
    FOREIGN KEY (user_id) REFERENCES users(user_id),
    FOREIGN KEY (category_id) REFERENCES category(category_id)
);

